mapper.select:
select count (*)
from PM_MEMBER
where PM_USER_NAME=#{pmUserName}
and PM_USER_BD=#{pmUserBd}
and PM_USER_ID=#{pmUserId}
and PM_USER_EMAIL=#{pmUserEmail}
and PM_USER_PHONE=#{pmUserPhone}

mapper.update:
update PM_MEMBER
set PM_USER_PWD=#{pmUserPwd}
where PM_USER_ID=#{pmUserId}

mapper.select:
SELECT PM_USER_ID
FROM PM_MEMBER
WHERE PM_USER_NAME =#{pmUserName}
AND PM_USER_EMAIL =#{pmUserEmail}
AND PM_USER_PHONE =#{pmUserPhone}
AND PM_USER_BD =#{pmUserBd};
